import React,{useState,useEffect} from 'react';
import { useWeb3React } from '@web3-react/core';
import { ethers } from "ethers";
import {contractAddress,abi} from '../utils/contractDetails';
const DashBoard = () => {
    const {account} = useWeb3React();
    const [contract, setContract] = useState();
    useEffect(()=>{
        const provider = new ethers.providers.Web3Provider(window.ethereum);
        const contract = new ethers.Contract(contractAddress, abi, provider);
        setContract(contract);
    },[account])
  return (
    <div>
        <form>
            <div className="form-group">
                <label htmlFor="exampleInputEmail1">your Name</label>
                <input type="email" className="form-control clientForm" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="your Name" />
            </div>
            <div className="form-group">
                <label htmlFor="exampleInputPassword1">Password</label>
                <input type="password" className="form-control clientForm" id="exampleInputPassword1" placeholder="Password" />
            </div>
            <div className="form-check">
                <label className="form-check-label" htmlFor="exampleCheck1">Check me out</label>
            </div>
            <button type="submit" className="btn btn-primary">Submit</button>
        </form>
    </div>
  )
}

export default DashBoard
